widgets.onValue:
  var beats = get(this)
  var bpm= 120
  var bpm2=get('tempo.label')
  bpm=Number(bpm)
  		var measure = Math.floor((beats+4)/4)
 			var beat =Math.floor(beats+1)- (measure-1)*4
 			var seconds = Math.floor((beats/bpm)*60)
  		var mins = Math.floor(seconds/60)  			 
  		var hours = Math.floor(seconds/3600)
  			if (hours<10)  {hours ='0'+hours}
  		var min= mins-hours*60
  			if (min<10)  {min ='0'+min}
  		var sec=seconds- mins*60
  			if (sec<10)  {sec ='0'+sec}
  			var aff= "Time : "+hours+" : "+min+" : "+sec+"   ||   Beat : "+measure+" . "+beat
set('time', aff)

