widgets.onValue:
var id = get('playing_no')
var cno = get('cueno_@{parent.variables.n}')
if(cno ==id) 
{set ('cueled_@{parent.variables.n}',1)}
else
{set ('cueled_@{parent.variables.n}',0)}

var nextn = value
nextn = nextn.replace('GO ','')
var hi = get('cues_count')
set ('text_1',nextn)

for (var i=1; i<hi; i++) 
{
   var numb = get('cueno_'+i)
   var note = get('cuenote_'+i)
  if (numb == nextn)
  {set ('notes_nextcue',note)}
}

