widgets.onValue:
var nextno = get(this)
nextno = nextno.replace('GO ','')
var max=get('cuemax')
for (var i=1;i<=max;i++){
if (nextno==get('cueno_'+i))
{ 
  var note= get('cuetrignote_'+i)
set ('notes_nextcue',note)}
}

var id = get('playing_no')
var cno = get('cueno_@{parent.variables.n}')
if(cno ==id) 
{set ('cueled_@{parent.variables.n}',1)}
else
{set ('cueled_@{parent.variables.n}',0)}

