}}

widgets.colorFill:
JS{{
var n=@{parent.variables.n}
var bg1= '#313131';
var bg2=@{cuecol_var_@{parent.variables.n}}
var val= @{butt_bgColor};
if(val===0.8) {var col=bg2};
if(val===0) {var col=bg1};
return col
}}
